<?php
// download.php - Mobile config download page
require_once 'config.php';

$deviceId = $_COOKIE['device_id'] ?? null;

if (!$deviceId) {
    header('Location: index.php');
    exit;
}

// Generate mobileconfig file
if (isset($_GET['download'])) {
    $mobileconfig = generateMobileConfig($deviceId);
    
    header('Content-Type: application/x-apple-aspen-config');
    header('Content-Disposition: attachment; filename="CouplesQuest.mobileconfig"');
    echo $mobileconfig;
    exit;
}

function generateMobileConfig($deviceId) {
    $uuid1 = generateUUID();
    $uuid2 = generateUUID();
    $uuid3 = generateUUID();
    
    $config = '<?xml version="1.0" encoding="UTF-8"?>
<!DOCTYPE plist PUBLIC "-//Apple//DTD PLIST 1.0//EN" "http://www.apple.com/DTDs/PropertyList-1.0.dtd">
<plist version="1.0">
<dict>
    <key>PayloadContent</key>
    <array>
        <dict>
            <key>PayloadDescription</key>
            <string>Adds The Couple\'s Quest web app to home screen</string>
            <key>PayloadDisplayName</key>
            <string>The Couple\'s Quest</string>
            <key>PayloadIdentifier</key>
            <string>com.thecouplesquest.webapp.' . $uuid2 . '</string>
            <key>PayloadType</key>
            <string>com.apple.webClip.managed</string>
            <key>PayloadUUID</key>
            <string>' . $uuid2 . '</string>
            <key>PayloadVersion</key>
            <integer>1</integer>
            <key>URL</key>
            <string>' . Config::SITE_URL . '/game.php?device_id=' . urlencode($deviceId) . '</string>
            <key>Label</key>
            <string>TCQ</string>
            <key>Icon</key>
            <data></data>
            <key>IsRemovable</key>
            <false/>
            <key>FullScreen</key>
            <true/>
        </dict>
    </array>
    <key>PayloadDescription</key>
    <string>Install The Couple\'s Quest web app</string>
    <key>PayloadDisplayName</key>
    <string>The Couple\'s Quest</string>
    <key>PayloadIdentifier</key>
    <string>com.thecouplesquest.webapp.' . $uuid1 . '</string>
    <key>PayloadRemovalDisallowed</key>
    <false/>
    <key>PayloadType</key>
    <string>Configuration</string>
    <key>PayloadUUID</key>
    <string>' . $uuid1 . '</string>
    <key>PayloadVersion</key>
    <integer>1</integer>
</dict>
</plist>';
    
    return $config;
}

function generateUUID() {
    return sprintf('%04x%04x-%04x-%04x-%04x-%04x%04x%04x',
        mt_rand(0, 0xffff), mt_rand(0, 0xffff),
        mt_rand(0, 0xffff),
        mt_rand(0, 0x0fff) | 0x4000,
        mt_rand(0, 0x3fff) | 0x8000,
        mt_rand(0, 0xffff), mt_rand(0, 0xffff), mt_rand(0, 0xffff)
    );
}
